import React from 'react';
import { StyleSheet, Text, View, TextInput } from 'react-native';

const MainPage  = () => {
    const [text, onChangeText] = React.useState("Test");


    return (
        <View style={styles.mainPage}>
          <Text style={styles.textbox}>Please scan the asset tag of the device</Text>

          <TextInput 
              style={styles.input}
              onChangeText = {onChangeText}
              value = {text}
          />
        </View>
    );
}

const styles = StyleSheet.create({
    mainPage : {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
    },

    textbox : {
        color: 'red',
        font : 'sans-serif'
    },

    input : {
        color : 'coral',
        borderColor: 'black',
        borderWidth: 1,
        borderRadius : 5,
        height: 100,
        width: 700,
        fontSize:70,
        textAlign : 'center'
    },
});

export default MainPage;
